Bash(/tmp/enum_record.bl << 'EOF'
@prelude\(core\)

type Status = | Pending | InProgress | Done

fn status_to_string\(s: Status\) -> String =
  match s
    Pending -> "pending"
    InProgress -> "in_progress"
    Done -> "done"

fn make_todo\(id: Int, title: String, s: Status\) -> Unknown = {
  let status_str = status_to_string\(s\)
  { id: id, title: title, status: status_str }
}

test "make todo pending" = {
  let todo = make_todo\(1, "Test", Pending\)
  todo.status == "pending"
}

test "make todo done" = {
  let todo = make_todo\(2, "Ship it", Done\)
  todo.status == "done"
}
EOF)
